  static AnimeReview? getAnimeById(int id) {
    try {
      return animeReviews.firstWhere((anime) => anime.id == id);
    } catch (e) {
      return null;
    }
  }

  static List<AnimeReview> getAllAnimes() {
    return List.from(animeReviews);
  }
}
